import React, { useState } from "react";
import TaskInput from "./For13Task/TaskInput";

function Home13() {
  const categories = [
    { id: "personal", name: "Ð”Ð¾Ð¼Ð°ÑˆÐ½Ð¸Ðµ Ð´ÐµÐ»Ð°", icon: "ðŸ " },
    { id: "work", name: "Ð Ð°Ð±Ð¾Ñ‡Ð¸Ðµ Ð¿Ñ€Ð¾Ñ†ÐµÑÑÑ‹", icon: "ðŸ’¼" },
    { id: "study", name: "Ð¢Ñ€ÐµÐ½Ð¸Ñ€Ð¾Ð²ÐºÐ¸", icon: "ðŸ“š" },
    { id: "leisure", name: "Ð ÐµÐ»Ð°ÐºÑ", icon: "â›±ï¸" },
  ];
  const [selectedCategory, setSelectedCategory] = useState(categories[0].id);
  const [tasks, setTasks] = useState([]);

  const handleCategoryClick = (categoryId) => {
    setSelectedCategory(categoryId);
  };

  const handleAddTask = (task) => {
    setTasks([...tasks, task]);
  };

  const filteredTasks = tasks.filter(task => task.category === selectedCategory);

  return (
    <div>
      <nav>
        {categories.map(category => (
          <button key={category.id} onClick={() => handleCategoryClick(category.id)}>
            {category.icon} {category.name}
          </button>
        ))}
      </nav>
      <ul>
        {filteredTasks.map(task => (
          <li key={task.id}>{task.title}</li>
        ))}
      </ul>
      <TaskInput onAddTask={handleAddTask} selectedCategory={selectedCategory} />
    </div>
  );
};


export default Home13;
